function getlink(){var e=document.createElement("input");e.setAttribute("value",window.location.href),document.body.appendChild(e),e.select(),document.execCommand("copy"),document.body.removeChild(e)}let copys=document.querySelectorAll(".copy");copys.forEach(e=>{e.addEventListener("click",()=>{document.getElementById("copy-msg").classList.add("copy-show"),getlink(),setTimeout(()=>{document.getElementById("copy-msg").classList.remove("copy-show")},1500)})});