function guardarNoticia(value,el){const formData=value,xhttp=new XMLHttpRequest;xhttp.onreadystatechange=function(){if(4==this.readyState&&200==this.status&&(document.getElementById("response-news").innerHTML=this.responseText,el.classList.contains("saved")?(el.innerHTML='<i class="fas fa-heart icon"></i>',el.setAttribute("title","Guardada en Favoritos")):(el.innerHTML='<i class="far fa-heart icon"></i>',el.setAttribute("title","Marcar como Favorita"))),xhttp.responseText.indexOf("<script>")>-1){var x=xhttp.responseText.indexOf("<script>")+"<script>".length,y=xhttp.responseText.indexOf("</script>")-x;eval(xhttp.responseText.substr(x,y))}},xhttp.open("POST","../php/save-news.php"),xhttp.send(formData)}let lists=document.querySelectorAll(".filter-card"),itemBox=document.querySelectorAll(".main-card");for(let e=0;e<lists.length;e++)lists[e].addEventListener("click",()=>{for(let e=0;e<lists.length;e++)lists[e].classList.remove("selected");lists[e].classList.add("selected"),document.querySelector(".news-main-container").classList.add("active"),setTimeout(()=>{document.querySelector(".news-main-container").classList.remove("active")},400);let t=lists[e].getAttribute("data-filter");for(let e=0;e<itemBox.length;e++)itemBox[e].classList.add("hide"),itemBox[e].classList.remove("selected"),itemBox[e].getAttribute("data-item")!=t&&"Todas"!=t||(itemBox[e].classList.add("selected"),itemBox[e].classList.remove("hide"))});let saveNews=document.querySelectorAll(".save-news");saveNews.forEach(e=>{e.addEventListener("click",t=>{t.preventDefault(),e.innerHTML='<i class="fas fa-spinner icon loading"></i>';let s=e.parentElement,i=new FormData(s);guardarNoticia(i,e)})});